import React, { useState } from "react";
import './ExpanseFrom.css'
import * as constants from "constants";

function ExpanseForm() {

    const [enteredTitle, setEnteredTitle] = useState('4')
    const [enteredAmount, setEnteredAmount] = useState('')
    const [enteredDate, setEnteredDate] = useState('')

    const titleChangeHandler = function (event) {
        setEnteredTitle(event.target.value);
        console.log(event.target.value)
        console.log(enteredTitle);
    }

    const amountChangeHandler = function (event) {
        setEnteredAmount(event.target.value)
    }

    const dateChangeHandler = function (event) {
        setEnteredDate(event.target.value)
    }
    return <div>
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange={titleChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expanse</button>
            </div>
        </form>
    </div>
}

export default ExpanseForm
