import React, { useState } from "react";
import './ExpanseFrom.css'

function ExpanseForm() {

    /////////////////// All 'stets' are defined separately ////////////////////

    // const [enteredTitle, setEnteredTitle] = useState('')
    // const [enteredAmount, setEnteredAmount] = useState('')
    // const [enteredDate, setEnteredDate] = useState('')

    /////////////////// All 'stets' are defined as a one in one object ////////////////////

    const [userInput, setUserInput] = useState({
        enteredTitle: '',
        enteredAmount: '',
        enteredDate: '',
    })

    const titleChangeHandler = function (event) {
        // setEnteredTitle(event.target.value);
        setUserInput({...userInput,                    // spread the 'useInput' object to change the state of only one value 'title'
            enteredTitle: event.target.value,})
    }

    const amountChangeHandler = function (event) {
        // setEnteredAmount(event.target.value)
        setUserInput({...userInput,
        enteredAmount: event.target.value,})
    }

    const dateChangeHandler = function (event) {
        // setEnteredDate(event.target.value)
        setUserInput({...userInput,
            enteredDate: event.target.value,})
    }
    return <div>
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange={titleChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}/>
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler}/>
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expanse</button>
            </div>
        </form>
    </div>
}

export default ExpanseForm
